    private void btnCancelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCancelActionPerformed
        
        int result = JOptionPane.showConfirmDialog(rootPane, "Deseja sair?", "Confirmação de saída", ConfirmationCallback.YES_NO_OPTION);
        if (result == JOptionPane.YES_OPTION) {
            System.exit(0);
        }
    }//GEN-LAST:event_btnCancelActionPerformed

    private void ckbAceitoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ckbAceitoActionPerformed

        if (ckbAceito.isSelected()) {
            btnNext.setEnabled(true);
        } else {
            btnNext.setEnabled(false);
        }
    }//GEN-LAST:event_ckbAceitoActionPerformed

